import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import MainPageContainer from "./containers/MainPageContainer";
import HeaderContainer from "./containers/HeaderContainer"
import LoginFormContainer from "./containers/LoginFormContainer";
import GroupPageContainer from "./containers/GroupPageContainer";
import About from './components/MainPage/About.js'
import UserProfileContainer from "./containers/UserProfileContainer";
import VotingContainer from "./containers/VotingContainer";
import {BrowserRouter as Router, Route} from "react-router-dom";
// import containers
import "./App.css";

class App extends Component {

  constructor(props) {
    super(props)
    this.state = {
      current: 'Keith'
    }
  }

  componentDidMount() {
    console.log(this.state.current);
    this.props.getData()
    this.props.getTasksData()
    //anything you want to run straight away
  }

  render() {
    return (
      <div className="App">
        <Router>
          <Fragment>
            <Route
              path='/'
              component={HeaderContainer}
            />
            <Route
              exact path='/'
              component={LoginFormContainer}
            />
            <Route
              exact path='/mainpage'
              render={(routeProps) => (
                <MainPageContainer {...routeProps} {...this.state} />
              )}
            />
            <Route
              exact path='/groups'
              component={GroupPageContainer}
            />
            <Route
              exact path='/about'
              component={About}
            />
            <Route
              exact path='/profile'
              component={UserProfileContainer}
            />
            <Route
              exact path='/voting'
              component={VotingContainer}
            />
          </Fragment>
        </Router>
      </div>
    );
  }

}

const mapDispatchToProps = dispatch => {
  return {
    getData() {
      dispatch(() =>{
        fetch('http://localhost:3000/api/users')
        .then(res => {
          return res.json().then(users => {
          dispatch({
            type:'ADD_USERS',
            users
          })
        })
        })
      })
    },
    getTasksData() {
      dispatch(() =>{
        fetch('http://localhost:3000/api/tasks')
        .then(res => {
          return res.json().then(tasks => {
          dispatch({
            type:'ADD_TASKS',
            tasks
          })
        })
        })
      })
    }

  }
}


export default connect(
  null,
  mapDispatchToProps
)(App);
